import logo from "./logo.svg";
import "./App.css";
import React, { useState } from "react";
import { v4 as uuidv4 } from "uuid";

const initialList = [
  {
    id: "a",
    name: "Sleepingbag",
    weight: 1000,
  },
  {
    id: "b",
    name: "Tent",
    weight: 2000,
  },
];

let finalTotal = 0

const weightTotal = initialList.map(c=>finalTotal += c.weight);
console.log()

function App() {
  const [list, setList] = useState(initialList);
  const [name, setName] = useState("");
  const [weight, setWeight] = useState(0);
  const [total, setTotal] = useState(0);

  //console.log(initialList.map(x => console.log(x.weight)));

  function handleChange(e) {
    setName(e.target.value);
  }

  function handleWeightChange(e) {
    setWeight(e.target.value);
  }

  function handleAdd() {
    const newList = list.concat({ weight, name, id: uuidv4() });

    setList(newList);

    setName("");
  }

  function calculateTotal(e) {
    setTotal(weight);
  }

  /*const meals = [
    { calorie: 10},
    { calorie: 15},
    { calorie: 20}
  ]; 
  const weightTotal = total.reduce((weightTotal, total) => totalCalories + meal.calorie, 0);

  console.log(calorieTotal); // 45 calories

  const mealsAsStrings = [
    { calorie: '11'},
    { calorie: '12'},
    { calorie: '13'}
  ];

  const calorieStringTotal = mealsAsStrings.reduce((totalCalories, meal) => totalCalories + parseInt(meal.calorie, 10), 0);

  console.log(calorieStringTotal); // 36 calories
*/

  return (
    <div className="App">
      <header className="App-header">
        <h1>Backpack App</h1>
        <br />
        <br />
        <h2>Organize your Backpack for your next Hike</h2>
        <br />
        <br />
        <div>
          <input type="text" value={name} onChange={handleChange} />
          <input type="number" value={weight} onChange={handleWeightChange} />
          <button type="button" onClick={handleAdd}>
            Add to the Backpack!
          </button>
        </div>
        <ul>
          {list.map((item) => (
            <li key={item.id}>
              {item.name} {item.weight} g
            </li>
          ))}
        </ul>

        <h2>Total weight: {total} kg</h2>
      </header>
    </div>
  );
}

export default App;
